mapper.select:
SELECT board_id boardId, board_title boardTitle, insert_date insertDate FROM board
board_title LIKE CONCAT('%',#{searchWord},'%')
Order BY board_id DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO board( 
	staff_id, board_pw, board_title, board_content, insert_date
) VALUES (
	#{staffId}, PASSWORD(#{boardPw}), #{boardTitle}, #{boardContent}, now()
)

mapper.delete:
DELETE FROM board 
WHERE board_id = #{boardID} 
AND board_pw = PASSWORD(#{boardPW})
